mapper.select:
/* CodeMapper.getSchCds */ SELECT SCH_CD ,SCH_FULL_NAME ,SCH_NAME FROM TS_SCH_CODE WHERE DEL_CD = 'N'
AND SCH_CD = #{sch_cd}
AND SCH_NAME LIKE CONCAT( '%', #{sch_name} ,'%' )

/* CodeMapper.getCommCds */ SELECT leader_cd ,comm_cd ,cd_name ,definition ,cd_order FROM TS_COMM_CODE WHERE del_yn = 'N'
AND leader_cd = #{leader_cd}
AND comm_cd = #{comm_cd}
ORDER BY cd_order ASC
